import React from 'react'
import "../styles/matchScene.css";
import { PlusCircle } from 'react-bootstrap-icons'
import { useState } from 'react';


export default function MatchCard({names}) {
  const [allowPlus, setAllowPlus] = useState(true);
  const [selectedFighter, setSelectedFighter] = useState({id: "", title: "", chip: 0});
  const [isConfirmed, setIsConfirmed] = useState(false);
  const [fighterId, setFighterId] = useState("");

  const handlePlus = () => {
    if (allowPlus){
      setAllowPlus(false);
    }
    else {
      setAllowPlus(true);
      // setSelectedFighter({id: "", title: "", chip: 0, bet: 0});
      setFighterId({fighterId: "", bet: 0});
    }
    setIsConfirmed(false);
  }  
  const handleConfirm = (event) => {
    event.preventDefault();
    // setAllowPlus(true);
    console.log(fighterId);
    const bot = names.find(name => name.id === fighterId.fighterId);
    if (bot !== undefined){
      setSelectedFighter({id: bot.id, title: bot.title, chip: bot.chip, bet: 0});
      setIsConfirmed(true);
    }

  }

  const selectFighter = (event) => {
      // const {inputID, inputBet} = event.target.value;
      // console.log(inputID, inputBet);
      // if (event.target.value === ""){
      //   // setSelectedFighter({id: "", title: "", chip: 0, bet: 0});
      //   setFighterId({id: "", bet: 0})
      // }
      // else {
      //   setFighterId(prevState => ({ ...prevState, [event.target.name]: event.target.value }));
      //   // console.log(fighterId);
      // }
      // console.log(selectedFighter);
      // console.log(event.target.value);
      setFighterId(prevState => ({ ...prevState, [event.target.name]: event.target.value }));
  }

  return (
    <div className='matchCard'>
      {allowPlus ? (
          <PlusCircle className='plus-button' onClick={handlePlus}></PlusCircle>
        ) : (
          <>
          { !isConfirmed ? (
            <form  onSubmit={handleConfirm} >
              <select 
                name="fighterId"
                value={fighterId.id}
                onChange={selectFighter}
              >
                <option selected value="">Select Bot</option>
                {names.filter((name) => name.chip !== "0").map(name => (
                  <option value={name.id}>{name.title}</option>
                ))}
              </select>

              {fighterId.title} &nbsp;
              <input
                  type="number"
                  name="bet"
                  autocomplete="off"
                  defaultValue="1"
                  value={fighterId.token}
                  max= {matchInput.chip}//{matchInput === {}? 1: names.find((name) => name.id === matchInput.fighterId).chip}
                  min="1"
                  required
                />
              <button type='submit'>Confirm</button>
              <button onClick={handlePlus}>Cancel</button>
            </form>
          ) : (
              <div>
                Fighter 1 {selectedFighter.title} {}
                <button onClick={handlePlus}>Remove</button>
              </div>
          )}
          </>
        )}

    </div>
  )
}
