import React from 'react'
import "../styles/matchScene.css";
import { PlusCircle } from 'react-bootstrap-icons'
import { useState } from 'react';


export default function MatchCard({names}) {
  const [allowPlus, setAllowPlus] = useState(false);
  const [selectedFighter, setSelectedFighter] = useState({id: "", title: "", chip: 0});
  const [isConfirmed, setIsConfirmed] = useState(false);

  const handlePlus = () => {
    if (!allowPlus){
      setAllowPlus(true);
    }
    else {
      setAllowPlus(false);
      setSelectedFighter({id: "", title: "", chip: 0});
    }

    setIsConfirmed(true);
  }  
  const handleConfirm = () => {
    setAllowPlus(false);
    setIsConfirmed(true);
  }

  const selectFighter = (event) => {
      if (event.target.value === ""){
        setSelectedFighter({id: "", title: "", chip: 0});
      }
      else {
        const bot = names.find(name => name.id === event.target.value);
        setSelectedFighter(bot);
      }
      console.log(selectedFighter);
  }

  return (
    <div className='matchCard'>
      {!allowPlus ? (
          <PlusCircle className='plus-button' onClick={handlePlus}></PlusCircle>
        ) : (
          <div>
            <select
              onChange={selectFighter}
            >
              <option selected value="">Select Bot</option>
              {names.filter((name) => name.chip !== "0").map(name => (
                <option value={name.id}>{name.title}</option>
              ))}
            </select>
            <button onClick={handleConfirm}>Confirm</button>
            <button onClick={handlePlus}>Cancel</button>
            <div>
              {selectedFighter.title} &nbsp;
              <input
                  type="number"
                  name="bet"
                  autocomplete="off"
                  defaultValue="1"
                  max={selectedFighter.chip}
                  min="1"
                  required
                />
            </div>
          </div>

        )}

    </div>
  )
}
