import React from 'react'
import "../styles/matchScene.css";
import { PlusCircle } from 'react-bootstrap-icons'
import { useState } from 'react';


export default function MatchCard({names}) {
  const [allowPlus, setAllowPlus] = useState(true);
  const [selectedFighter, setSelectedFighter] = useState({id: "", title: "", chip: 0});
  const [isConfirmed, setIsConfirmed] = useState(false);
  const [fighterId, setFighterId] = useState("");
  const [bet, setBet] = useState(1);

  const handlePlus = () => {
    if (allowPlus){
      setAllowPlus(false);
    }
    else {
      setAllowPlus(true);
      // setSelectedFighter({id: "", title: "", chip: 0, bet: 0});
      setFighterId("");
      setBet(1);
    }
    setIsConfirmed(false);
  }  
  const handleConfirm = (event) => {
    event.preventDefault();

    const bot = names.find(name => name.id === fighterId);
    if (bot !== undefined){
      setSelectedFighter({id: bot.id, title: bot.title, chip: bot.chip, bet: bet});
      setIsConfirmed(true);
    }

  }

  const handleFighterId = (event) => {
      setFighterId(event.target.value);
      setBet(1);
  }

  return (
    <div className='matchCard'>
      {allowPlus ? (
          <PlusCircle className='plus-button' onClick={handlePlus}></PlusCircle>
        ) : (
          <>
          { !isConfirmed ? (
            <div>
              <select 
                name="fighterId"
                value={fighterId}
                onChange={handleFighterId}
              >
                <option value="">Select Bot</option>
                {names.filter((name) => name.chip !== "0").map(name => (
                  <option value={name.id}>{name.title}</option>
                ))}
              </select>

              <input
                  type="number"
                  name="bet"
                  autocomplete="off"
                  value={bet}
                  max= {fighterId === ""? 1: names.find((name) => name.id === fighterId).chip}
                  min="1"
                  onChange={(e)=>{setBet(e.target.value)}}
                  required
                />
              <button onClick={handleConfirm} type='submit'>Confirm</button>
              <button onClick={handlePlus}>Cancel</button>
            </div>
          ) : (
              <div>
                Fighter 1: {selectedFighter.title} {selectedFighter.bet} {selectedFighter.chip} {}
                <button onClick={handlePlus}>Remove</button>
              </div>
          )}
          </>
        )}

    </div>
  )
}
