import React from 'react'
import PropTypes from 'prop-types'
import '../styles/card.css'
import {v4 as uuidv4} from "uuid";
import BotList from './BotList';

export default function Card({input, setInput, names, setNames}) {

  const onInputChange = (event) => {
    // setInput(event.target.value);
    setInput({[event.target.name]: event.target.value })
  };

  const onFormSubmit = (event) => {
    event.preventDefault();
    setNames([...names, {id: uuidv4(), title: input.botname, chip: input.chipnum}])
    setInput("")
  };


  return (
    <>
      <div className="card-body">
          <h2>Participants</h2>
          <form onSubmit={onFormSubmit} onChange={onInputChange}>
            <div className='container'>
              <input
                type="text"
                placeholder="Add Bot Name"
                name="botname"
                className='bot-input'
                value={input.botname}
                onChange={onInputChange}
                required
              />
              <input
                type="number"
                placeholder="3"
                name="chipnum"
                className='num-input'
                value={input.chipnum}
                onChange={onInputChange}
                required
              />
              <button className='button-style' type="submit">Enter</button>
            </div>
          </form>
        <BotList names={names} setNames={setNames}/>
      </div>



    </>
  )
}

Card.propTypes = {
    name: PropTypes.string
}

Card.defaultProps = {
    name: "Name"
}







