import '../styles/winTog.css';
import React, { useState } from 'react';

export default function SwitchToggle() {
  const [toggleState, setToggleState] = useState('na');

  const handleToggleChange = (e) => {
    setToggleState(e.target.id);
  };

  return (
    <div className="switch-toggle">
      <input
        id="red"
        name="state-d"
        type="radio"
        checked={toggleState === 'red'}
        onChange={handleToggleChange}
      />
      <label htmlFor="red">RED</label>

      <input
        id="na"
        name="state-d"
        type="radio"
        disabled
        checked={toggleState === 'na'}
        onChange={handleToggleChange}
      />
      <label htmlFor="na" className="disabled">W</label>

      <input
        id="blue"
        name="state-d"
        type="radio"
        checked={toggleState === 'blue'}
        onChange={handleToggleChange}
      />
      <label htmlFor="blue">BLUE</label>

    </div>
  );
}
