import React, {useState} from 'react'

export default function AddCard() {

    const [name, addName] = useState("Enter Name Here");

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            console.log("enter pressed");
            addName(event.target.value);
        }
    };

    return (
        <div className="form-floating mb-3">
            <input className="form-control" id="mybox" onKeyDown={handleKeyDown}/>
            <label htmlFor="mybox">Enter Name</label>
            <h1>{name}</h1>
        </div>
