import React from 'react'
import { Trash } from 'react-bootstrap-icons'
import { PencilSquare } from 'react-bootstrap-icons'

const BotList = ({names, setNames}) => {

  const [editingId, setEditingId] = useState(null);
  const [newName, setNewName] = useState("");

  const handleDelete = ({id}) => {
    setNames(names.filter((name) => name.id !== id));
  };

  const handleEdit = id => {
    setEditingId(id);
    setNewName(names.find(name => name.id === id).name);
  };

  const handleSave = () => {
    setNames(
      names.map(name =>
        name.id === editingId
          ? { ...name, name: newName }
          : name
      )
    );
    setEditingId(null);
  };


  return (
    <div>
      {names.map((name) => (
          <li className='list-item container' key={name.id}>
              {/* <input
                type="text"
                value={name.title}
                //onChange = {(event) => event.preventDefault()}
              />
              <div>
              <input
                type="text"
                value={name.chip}
              /> */}
              {name.title} 
              <div>
                {name.chip} &nbsp;
                <PencilSquare className='button-delete-edit' onClick={() => handleEdit(name)}/>
                <Trash className='button-delete-edit' onClick={() => handleDelete(name)}/>
              </div>
          </li>
        )
      )}
    </div>
  )
}


export default BotList
