import React from 'react'
import "../styles/matchScene.css";
import { PlusCircle } from 'react-bootstrap-icons'
import { useState } from 'react';


export default function MatchCard({names}) {
  const [allowPlus, setAllowPlus] = useState(false);
  const [selectedFighter, setSelectedFighter] = useState({id: "", title: ""});

  const handlePlus = () => {
    if (!allowPlus){
      setAllowPlus(true);
    }
    else {
      setAllowPlus(false);
    }
  }  

  const selectFighter = (event) => {
      const bot = names.find(name => name.id === event.target.value? name: {id: "", title: ""});
      setSelectedFighter(bot);
      console.log(selectedFighter);
  }

  return (
    <div className='matchCard'>
      {!allowPlus ? (
          <PlusCircle className='plus-button' onClick={handlePlus}></PlusCircle>
        ) : (
          <div>
            <select
              onChange={selectFighter}
            >
              <option selected value="">Select Bot</option>
              {names.reverse().map(name => (
                <option value={name.id}>{name.title}</option>
              ))}
            </select>
            {selectedFighter.title}
            <button onClick={handlePlus}>Cancel</button>
          </div>

        )}

    </div>
  )
}
