import React from 'react'
import "../styles/matchScene.css";
import { PlusCircle } from 'react-bootstrap-icons'
import { useState } from 'react';


export default function MatchCard({names}) {
  const [allowPlus, setAllowPlus] = useState(false);
  const [selectedFighter, setSelectedFighter] = useState(null);

  const handlePlus = () => {
      setAllowPlus(true);
  }  

  const selectFighter = (event) => {
      const bot = names.find(name => name.id === event.target.value);
      console.log(bot);
      setSelectedFighter(bot);
  }

  return (
    <div className='matchCard'>
      {!allowPlus ? (
          <PlusCircle className='plus-button' onClick={handlePlus}></PlusCircle>
        ) : (
          <div>
            <select
              onChange={selectFighter}
              defaultValue="Select"
              placeholder=''
            >
              {names.reverse().map(name => (
                <option value={name.id}>{name.title}</option>
              ))}
            </select>
            {/* {selectedFighter.name} */}
          </div>

        )}

    </div>
  )
}
