import React from 'react'
import PropTypes from 'prop-types'
import '../styles/card.css'
import {v4 as uuidv4} from "uuid";
import BotList from './BotList';

export default function Card({input, setInput, names, setNames}) {

  const onInputChange = (event) => {
    // setInput(event.target.value);
    setInput(prevState => ({ ...prevState, [event.target.name]: event.target.value }))
  };

  const onFormSubmit = (event) => {
    event.preventDefault();
    // setNames([...names, {id: uuidv4(), title: input.botname, chip: input.chipnum}].sort());
    const Names = [...names, {id: uuidv4(), title: input.botname, chip: input.chipnum}];
    setNames(Names);

    // Need to shift to a different place to account for editing as well
    handleSortName(Names);
    // setInput({botname: "", chipnum: "3"})
    setInput(Object.assign(...Object.keys(input).map(k => ({ [k]: "" }))));
  };

  const handleSortName = (names) => {
    // set the state to the newly created sorted array with the three dots operator:
    setNames(
      [...names].sort((a, b) => a.chip - b.chip)
    );
  };

  return (
    <>
      <div className="card-body">
          <h2>Participants</h2>
          <form onSubmit={onFormSubmit} onChange={onInputChange}>
            <div className='container'>
              <input
                type="text"
                placeholder="Add Bot Name"
                name="botname"
                className='bot-input'
                autocomplete="off"
                value={input.botname}
                required
              />
              <input
                type="number"
                placeholder="Chip"
                name="chipnum"
                className='num-input'
                autocomplete="off"
                value={input.chipnum}
                min="0"
                required
              />
              <button className='button-style' type="submit">Enter</button>
            </div>
          </form>
        <BotList 
            names={names} 
            setNames={setNames} 
            // handleSortName={handleSortName}
        />
      </div>
      <div className="footer">
          <p>Combat Ready Robotics @ ASU</p>
      </div>
    </>
  )
}

