import React, { useEffect, useState } from 'react';
import '../styles/timer.css';

export default function Timer() {

    const [input, setInput] = useState({});
    const [time, setTime] = useState({});
    // const [minutes, setMinutes] = useState(0);
    // const [seconds, setSeconds] = useState(0);
    const [counter, setCounter] = useState(0);
    const [timerState, setTimerState] = useState("reset");

    useEffect(() => {
        if (counter >= 0 && timerState === "start") {
            setTimeout(() => setCounter(counter-1), 1000);
            // setMinutes(Math.floor(counter / 60));
            // setSeconds(Math.floor(counter % 60));
            setTime({})
        }
    }, [counter]);

    const startTimer = () => {
        if ((minutes > 0 || seconds >> 0)){
            setTimerState("start"); 
        }
        setCounter(minutes * 60 + seconds);
    }
    const pauseTimer = () => {
        setTimerState("pause"); 
    }
    const resetTimer = () => {
        setTimerState("reset");
        setCounter(0);
        setMinutes(0);
        setSeconds(0);
    }

    const handleInput = (event) => {
        setInput(prevState => ({ ...prevState, [event.target.name]: event.target.value }))
    };

    return (
        <div className='timer'>
            <form>
            <input
                type="number"
                className='timer-input'
                name="minutes"
                autoComplete="off"
                value={minutes.toString().padStart(2, '0')}
                onChange={(e)=>{setMinutes(e.target.value)}}
                min="0"
                required/>
            <input
                type="number"
                className='timer-input'
                name="seconds"
                autoComplete="off"
                value={seconds.toString().padStart(2, '0')}
                onBlur={(e)=>{setSeconds(e.target.value)}}
                max="59"
                min="0"
                required/>
            </form>
            {(timerState === "start")? 
                    <button className='timer-bt' onClick={pauseTimer} >Pause</button>
                    : <button className='timer-bt' onClick={startTimer}>Start</button>}
            <button className='timer-bt' onClick={resetTimer}>Reset</button>
        </div>
    )
}
