import React, { useEffect, useState } from 'react';
import '../styles/timer.css';

export default function Timer() {

    const [input, setInput] = useState({minutes: 0, seconds: 0});
    // const [minutes, setMinutes] = useState(0);
    // const [seconds, setSeconds] = useState(0);
    const [counter, setCounter] = useState(0);
    const [timerState, setTimerState] = useState("reset");

    useEffect(() => {
        if (counter >= 0 && timerState === "start") {
            setTimeout(() => setCounter(counter-1), 1000);
            // setMinutes(Math.floor(counter / 60));
            // setSeconds(Math.floor(counter % 60));
            setTime({minutes: Math.floor(counter / 60), seconds: Math.floor(counter % 60)});
        }
    }, [counter]);

    const startTimer = () => {
        if ((time.minutes > 0 || time.seconds >> 0)){
            setTimerState("start"); 
        }
        setCounter(time.minutes * 60 + time.seconds);
    }
    const pauseTimer = () => {
        setTimerState("pause"); 
    }
    const resetTimer = () => {
        setTimerState("reset");
        setCounter(0);
        // setMinutes(0);
        // setSeconds(0);
        setTime({minutes: 0, seconds: 0});

    }

    const handleInput = (event) => {
        setInput(prevState => ({ ...prevState, [event.target.name]: event.target.value }))
    };
    const handleTime = (event) => {
        setTime({minutes: input.minutes.toString().padStart(2, '0'), seconds: input.seconds.toString().padStart(2, '0')});
    }

    return (
        <div className='timer'>
            <form>
            <input
                type="number"
                className='timer-input'
                name="minutes"
                autoComplete="off"
                value={time.minutes}
                onChange={handleInput}
                onBlur={handleTime}
                min="0"
                required/>
            <input
                type="number"
                className='timer-input'
                name="seconds"
                autoComplete="off"
                value={time.seconds}
                onChange={handleInput}
                onBlur={handleTime}
                max="59"
                min="0"
                required/>
            </form>
            {(timerState === "start")? 
                    <button className='timer-bt' onClick={pauseTimer} >Pause</button>
                    : <button className='timer-bt' onClick={startTimer}>Start</button>}
            <button className='timer-bt' onClick={resetTimer}>Reset</button>
        </div>
    )
}
