import React from 'react'
import { Trash } from 'react-bootstrap-icons'
import { PencilSquare } from 'react-bootstrap-icons'
import { useState } from 'react'

const BotList = ({names, setNames}) => {

  const [editingId, setEditingId] = useState(null);
  const [newName, setNewName] = useState({});

  const handleEdit = ({id}) => {
    setEditingId(id);
    const editedName = names.find(name => name.id === id);
    setNewName({"newbotname": editedName.title, "newchipnum":editedName.chip});
  };

  const handleSave = (event) => {
    event.preventDefault();
    setNames(
      names.map(name =>
        name.id === editingId
          ? { ...name, id: name.id, title: newName.newbotname , chip: newName.newchipnum}
          : name
      )
    );
    handleSortName(names);
    console.log(newName);
    console.log(names);
    setEditingId(null);
  };

  const handleSortName = (names) => {
    // set the state to the newly created sorted array with the three dots operator:
    setNames(
      [...names].sort((a, b) => a.chip - b.chip)
    );
  };

  const handleDelete = ({id}) => {
    setNames(names.filter((name) => name.id !== id));
  };

  const handleChange = (event) => {
    setNewName(prevState => ({ ...prevState, [event.target.name]: event.target.value }))
  };


  return (
    // <div>
    //   {names.map((name) => (
    //       <li className='list-item container' key={name.id}>
    //           {/* <input
    //             type="text"
    //             value={name.title}
    //             //onChange = {(event) => event.preventDefault()}
    //           />
    //           <div>
    //           <input
    //             type="text"
    //             value={name.chip}
    //           /> */}
    //           {name.title} 
    //           <div>
    //             {name.chip} &nbsp;
    //             <PencilSquare className='button-delete-edit' onClick={() => handleEdit(name)}/>
    //             <Trash className='button-delete-edit' onClick={() => handleDelete(name)}/>
    //           </div>
    //       </li>
    //     )
    //   )}
    // </div>

    <div>
        {names.map(name => (
          <li className='list-item container' key={name.id}>
            {editingId === name.id ? (
              <form className='edit-form' onSubmit={handleSave} onChange={handleChange}>
                <input
                  type="text"
                  className='text-edit-input'
                  name="newbotname"
                  autocomplete="off"
                  value={newName.newbotname} 
                  required
                />
                <input
                  type="number"
                  className='num-edit-input'
                  name="newchipnum"
                  autocomplete="off"
                  value={newName.newchipnum} 
                  min="0"
                  required
                />
                <button>Save</button>
              </form>
            ) : (
              <>
                {name.title} 
                <div>
                   {name.chip} &nbsp;
                   <PencilSquare className='button-delete-edit' onClick={() => handleEdit(name)}/>
                   <Trash className='button-delete-edit' onClick={() => handleDelete(name)}/>
                 </div>
              </>
            )}
          </li>
        ))}
    </div>
  )
}


export default BotList
