import React from 'react'
import "../styles/matchScene.css";
import { PlusCircle } from 'react-bootstrap-icons'
import { useState } from 'react';


export default function MatchCard({names}) {
  const [allowPlus, setAllowPlus] = useState(false);
  const [selectedFighter, setSelectedFighter] = useState({id: "", title: "", chip: 0, token: 0});
  const [isConfirmed, setIsConfirmed] = useState(false);

  const handlePlus = () => {
    if (allowPlus){
      setAllowPlus(false);
    }
    else {
      setAllowPlus(true);
      setSelectedFighter({id: "", title: "", chip: 0, token: 0});
    }
    setIsConfirmed(false);
  }  
  const handleConfirm = (event) => {
    event.preventDefault();
    // setAllowPlus(true);
    setIsConfirmed(true);
    console.log(event.target.name);
    console.log(event.target.value);

  }

  const selectFighter = (event) => {
      if (event.target.value === ""){
        setSelectedFighter({id: "", title: "", chip: 0, token: 0});
      }
      else {

        const bot = names.find(name => name.id === event.target.value);
        setSelectedFighter({id: bot.id, title: bot.title, chip: bot.chip, token: 0});
      }
      console.log(selectedFighter);
      console.log(event.target.value);
  }

  return (
    <div className='matchCard'>
      {allowPlus ? (
          <PlusCircle className='plus-button' onClick={handlePlus}></PlusCircle>
        ) : (
          <>
          { !isConfirmed ? (
            <form  onSubmit={handleConfirm}>
